import star from '../images/star.png'


export default function Card(props) {
    let badgeText
    if (props.item === 0) {
        badgeText = "SOLD OUT"
    }else if (props.location === "Online") {
        badgeText = "ONLINE"
    }
    else if (props.item ==="Norway") {
        badgeText = "NORWAY"
    }
    return (
       <div className="card">
           {badgeText && <div className="card--badge">{badgeText}</div>}

           <img  className="card--image" src={`../images/${props.item}`}/>
           <div className="card--status">
               <img   className="card--star" src={star}/>
               <span>{props.item}</span>
               <span className="gray">({props.item})â€¢</span>
               <span className="gray">{props.item}</span>
           </div>
           <p className="card-title">{props.item}</p>
           <p className="card-price"><span className="bold">From ${props.item}</span>/ person</p>
       </div>

    )
}
